import styled from 'styled-components'

export const StyledScore = styled.div`
    border: 1px solid #999;
    padding: 20px;
    margin: 0 auto;
    border-radius: 5px;
    background-color: lightgrey;
`;

export const StyledText = styled.p`
	width: 150px;
	float: left;
`;

export const StyledButton = styled.button`
  	width: calc(100% - 60%);
	height: 35px;
	cursor: pointer;
	color: black;
	font-size: 14px;
	border: 1px solid #999;
	border-radius: 3px;
	line-height: 30px;
	left: 10px;
`;
