import styled from 'styled-components'

export const StyledListTasks =  styled.div`
  position: relative;
  width: 100%;
  height: 50px;
  margin: 0 auto;
  margin-bottom: 20px;
`;

export const StyledTask =  styled.div`
  width: 32%;
  float: left;
  line-height: 50px;
  border: 1px solid gray;
  margin: 1px;
  border-radius: 5px;
  cursor: pointer;
  text-transform: capitalize;
`;
